import React, {useState} from "react";
import Base from "../core/Base";

import {Link} from "react-router-dom";

const Signin = () => {

    const [values, setValues] = useState({
        email: "",
        password: "",
        loading: false,
        didRedirect: false
    });

    const {email, password,error, loading, didRedirect} = values;
    const {user} = isAuthenticated();


     //higher order function hitesh chau. channel
     const handleChange = name => event => {
        setValues({...values, error: false, [name]: event.target.value})
     }

     const onSubmit = event =>{

        event.preventDefault();
        setValues({...values, error:false, loading:true})
        signin({email, password})
        .then(data => {
                if(data.error){
                    setValues({...values, error:data.error, loadi})
                }
        })

     }
      const successMessage = () => {
      return( 
          <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
      <div className="alert alert-success" 
        style={{display: success ? "" : "none"}}>

        New account was created successfully.Please{" "}
        <Link to= "/signin">Login Here</Link>
        </div>
        </div>
        </div>
        ) 
    }
     const errorMessage = () => {
        return(
             <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
            <div className="alert alert-danger" 
        style={{display: error ? "" : "none"}}>

        {error}
        </div>
        </div>
        </div>
        )
    }
        const signInForm = () => {

        return(
            <div className="row">
                <div className="col-md-6 offset-sm-3 text-left">
                    <form>
                        <div className="form-group">
                            <label className="text-light">Email</label>
                            <input onChange={handleChange("email")} value={email} className="form-control" type="email" />
                        </div>
                         <div className="form-group">
                            <label className="text-light">Password</label>
                            <input onChange={handleChange("password")} value={password} className="form-control" type="password" />
                        </div>
                        <br/>
                             <button onClick={onSubmit} className="btn btn-success btn-lg btn-block form-control">Submit</button>
                    </form>
                </div>
            </div>
        )
    }

    return (
        <Base title = "Sign up Page" description="A page for user to Sign in!">
            {signInForm()}
        </Base>
    );
};

export default Signin;
