import React, { useState } from "react";
import Base from "../core/Base";
import { isAuthenticated } from "../auth/helper";
import { Link } from "react-router-dom";
import {createCategory} from "./helper/adminapicall"

const AddCategory = () => {

  const [name, setName] = useState("initialState");
  const [error, setError] = useState(false);
  const [success, setSuccess] = useState(false);

  const { user, token } = isAuthenticated();

  const goBack = () => (
    <div className="mt-5">
      <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
        Admin Home
      </Link>
    </div>
  );

  const handleChange = event => {
      setError("");
      setName(event.target.value)
  }

  const onSubmit = (event) => {
    event.preventDefault();
    setError("");
    setSuccess(false)
    //backend request fired
    createCategory
  }

  const myCategoryForm = () => (
    <form>
      <div className="form-group"><br />
        <p className="lead text-center font-monospace">Enter the category</p>
       {/* <select className="form-control form-control-lg"><option >select</option> </select> */}
        <input
          type="text"
          className="form-control my-3"
          onChange={handleChange}
          value={name}
          autoFocus
          required
          placeholder="For Ex. Summer"
        />
        <button onClick={onSubmit} className="btn btn-outline-info">Create Category</button>
      </div>
      <br />
    </form>
  );

  return (
    <Base
      title="Create a category here"
      description="Add a new category for new tshirts"
      className="container bg-info p-4"
    >
      <div className="row bg-white rounded">
        <div className="col-md-8 offset-md-2">
          {myCategoryForm()}
          {goBack()}
        </div>
      </div>
    </Base>
  );
};

export default AddCategory;
